<style scoped lang="scss">
@import "@/style/common.scss";
.course-wrap {
  display: flex;
  justify-content: center;
}
.course-list {
  display: flex;
  gap: 16px;
  flex-wrap: wrap;
}
.course-card {
  width: 260px;
  height: 200px;
  cursor: pointer;
  border-radius: 8px;
  background-color: #fff;
  position: relative;
  &-bg{
    position: absolute;
    top: 5px;
    right: 6px;
    width: 62px;
    height: 60px;
  }
  &:hover {
    box-shadow: 0 4px 16px 0 rgba(0, 0, 0, 0.12);
  }
  .course-cover {
    width: 260px;
    height: 160px;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    overflow: hidden;
  }
  .course-name {
    font-weight: 500;
    margin-bottom: 8px;
  }
  .course-text-info {
    padding: 8px;
  }
  .course-info {
    display: flex;
    justify-content: space-between;
    margin-top: 12px;
    &-left {
      color: $active-color;
      font-size: 12px;
    }
    &-right {
      color: $text-plain-color;
      font-size: 12px;
    }
    &-item{
      color:$text-plain-color;
    }
  }
}
</style>
